class Admin{constructor(){this.announcement=JSON.parse(window.localStorage.getItem("announcement")||"null")}wait(a){return new Promise(b=>{setTimeout(()=>b(null),a)})}logError(a){const b=new Error(a);console.error(b)}removeAnnouncement(){this.announcement={type:"general",title:"",text:""},window.localStorage.removeItem("announcement")}setAnnouncement(a){this.announcement=a,window.localStorage.setItem("announcement",JSON.stringify(a))}}